const i = ntb.getActiveItem();
if (app.vault.getConfig('theme') === 'obsidian') { app.vault.setConfig('theme', 'obsidian-light'); i?.setIcon('moon'); }
else { app.vault.setConfig('theme', 'obsidian'); i?.setIcon('sun'); }

const wrap = (i, n) => ((i % n) + n) % n;

function navFile(s) {
    const cf = app.workspace.getActiveFile();
    const fs = cf?.parent?.children?.filter(f => !f.children) || [];
    const ci = fs.findIndex(f => f.path === cf.path);
    if (fs.length > 1)
        app.workspace.activeLeaf.openFile(fs[wrap(ci + s, fs.length)]);
}

// prev
navFile(-1);

